function GetToken(){const e=document.cookie.split(";");for(let t=0;t<e.length;t++){let n=e[t];for(;" "===n.charAt(0);)n=n.substring(1);if(0===n.indexOf("AUTHTKN="))return n.substring("AUTHTKN=".length,n.length)}if("localhost"===location.hostname){const e=window.localStorage.getItem("AUTHTKN");return e||""}return""}function SetToken(e){const t=GetJWT(e),n=new Date(0);n.setUTCSeconds(t.exp),document.cookie=`AUTHTKN=${e}; expires=${n.toUTCString()}; path=/`,"localhost"===location.hostname&&window.localStorage.setItem("AUTHTKN",e)}function GetJWT(e){const t=e.replace(/[+\/]/g,e=>"+"===e?"-":"_").replace(/=/g,"").split(".")[1].replace(/\s/g,"");return JSON.parse(window.atob(t.replace(/[-_]/g,e=>"-"===e?"+":"/").replace(/[^A-Za-z0-9\+\/]/g,"")))}function IsLoggedIn(){return""!==GetToken()}function UpdateUser(){const e=GetJWT(GetToken());ID=e.id,Email=e.email,FirstName=e.first_name,LastName=e.last_name,PhotoURL=e.photo_url}function HasCreditCard(){return getKthBit(GetJWT(GetToken()).perm,0)}function getKthBit(e,t){return 1==(e>>t&1)}function Login(e){return callFetch("/api/v1/Login","POST",{token:e}).then(e=>(e&&e.token&&SetToken(e.token),e))}function callFetch(e,t,n){return fetch(e,{method:t,headers:{"Content-Type":"application/json","auth-token":GetToken()},body:JSON.stringify(n)}).then(e=>e.json()).catch(e=>{console.error("failed to callFetch",e)})}addEventListener("UserUpdated",UpdateUser);let ID="",Email="",FirstName="",LastName="",PhotoURL="";UpdateUser();var user=Object.freeze({IsLoggedIn:IsLoggedIn,get ID(){return ID},get Email(){return Email},get FirstName(){return FirstName},get LastName(){return LastName},get PhotoURL(){return PhotoURL},UpdateUser:UpdateUser,HasCreditCard:HasCreditCard}),service=Object.freeze({Login:Login});APP.Service=service,APP.User=user,console.log("app.js loaded");